// Combine all AI research results into a structured format
const trendsData = $('AI Trends Research (GPT-4 with Web)').item.json;
const expertData = $('AI Expert Analysis (Claude with Web)').item.json;
const practicesData = $('AI Best Practices Research Anthropic').item.json;

// Extract content from different AI responses
let trendsContent = '';
let expertContent = '';
let practicesContent = '';

// Handle GPT-4 response
if (trendsData && trendsData.choices && trendsData.choices[0]) {
  trendsContent = trendsData.choices[0].message.content;
} else if (trendsData && trendsData.message) {
  trendsContent = trendsData.message.content;
}

// Handle Claude response
if (expertData && expertData.content) {
  expertContent = expertData.content;
} else if (expertData && expertData.message) {
  expertContent = expertData.message.content;
}

// Handle Perplexity response
if (practicesData && practicesData.choices && practicesData.choices[0]) {
  practicesContent = practicesData.choices[0].message.content;
} else if (practicesData && practicesData.message) {
  practicesContent = practicesData.message.content;
}

// Combine all research into a comprehensive summary
const combinedResearch = `
=== TRENDS AND DEVELOPMENTS RESEARCH ===
${trendsContent}

=== EXPERT ANALYSIS AND INSIGHTS ===
${expertContent}

=== BEST PRACTICES AND IMPLEMENTATION GUIDES ===
${practicesContent}
`;

return {
  combined_research: combinedResearch,
  trends_research: trendsContent,
  expert_analysis: expertContent,
  best_practices: practicesContent,
  topic: $('Manual Trigger').item.json.topic,
  research_sections: 3,
  timestamp: new Date().toISOString()
};

// Format the article content for Joomla with enhanced processing
const article = $('Generate Comprehensive Article').item.json.message.content;
const seoDataRaw = $json.message.content;
const originalTopic = $('Combine AI Research').item.json.topic;

// Parse SEO data (handle potential JSON parsing issues)
let seoData;
try {
  seoData = JSON.parse(seoDataRaw);
} catch (e) {
  // Fallback if JSON parsing fails
  seoData = {
    seo_title: originalTopic,
    social_title: originalTopic,
    meta_description: `Comprehensive guide to ${originalTopic}`,
    tags: [originalTopic.split(' ')[0], originalTopic.split(' ')[1] || 'guide'],
    alias: originalTopic.toLowerCase().replace(/[^a-z0-9]/g, '-').replace(/-+/g, '-'),
    excerpt: `Learn everything about ${originalTopic} in this comprehensive guide.`
  };
}

// Enhanced content formatting for Joomla
let formattedContent = article
  // Convert markdown headers to HTML
  .replace(/^### (.*$)/gm, '<h4>$1</h4>')
  .replace(/^## (.*$)/gm, '<h3>$1</h3>')
  .replace(/^# (.*$)/gm, '<h2>$1</h2>')
  // Convert bold text
  .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
  // Convert italic text
  .replace(/\*(.*?)\*/g, '<em>$1</em>')
  // Convert bullet points to HTML lists
  .replace(/^- (.*$)/gm, '<li>$1</li>')
  // Wrap consecutive list items in <ul> tags
  .replace(/(<li>.*<\/li>)(\s*<li>.*<\/li>)*/gs, '<ul>$&</ul>')
  // Convert paragraphs
  .replace(/\n\n+/g, '</p>\n\n<p>')
  .replace(/\n/g, '<br>')
  .replace(/^/, '<p>')
  .replace(/$/, '</p>');

// Clean up formatting issues
formattedContent = formattedContent
  .replace(/<p><h([1-6])>/g, '<h$1>')
  .replace(/<\/h([1-6])><\/p>/g, '</h$1>')
  .replace(/<p><ul>/g, '<ul>')
  .replace(/<\/ul><\/p>/g, '</ul>')
  .replace(/<p><\/p>/g, '')
  .replace(/<p>\s*<br>/g, '<p>')
  .replace(/<br>\s*<\/p>/g, '</p>')
  .replace(/<br>\s*<h([1-6])>/g, '<h$1>')
  .replace(/<\/h([1-6])>\s*<br>/g, '</h$1>');

// Create introtext (first paragraph or first 300 characters)
let introText = formattedContent.match(/<p>(.*?)<\/p>/)?.[1] || formattedContent.substring(0, 300);
if (introText.length > 300) {
  introText = introText.substring(0, 297) + '...';
}

// Generate current timestamp for created date
const now = new Date();
const mysqlDateTime = now.toISOString().slice(0, 19).replace('T', ' ');

return {
  title: seoData.seo_title,
  alias: seoData.alias,
  introtext: introText,
  fulltext: formattedContent,
  metadesc: seoData.meta_description,
  metakey: Array.isArray(seoData.tags) ? seoData.tags.join(', ') : seoData.tags,
  state: 1, // Published
  catid: $('Manual Trigger').item.json.categoryId || 2,
  created_by: $('Manual Trigger').item.json.authorId || 1,
  language: '*',
  access: 1,
  featured: $('Manual Trigger').item.json.featured || 0,
  created: mysqlDateTime,
  publish_up: mysqlDateTime,
  originalTopic: originalTopic,
  socialTitle: seoData.social_title,
  excerpt: seoData.excerpt,
  wordCount: article.split(' ').length,
  researched_by: 'AI Agents (GPT-4, Claude, Perplexity)'
};

// Prepare comprehensive workflow execution summary
const joomlaResponse = $('Publish to Joomla (API)').item.json;
const articleData = $('Format Content for Joomla').item.json;
const researchData = $('Combine AI Research').item.json;

// Determine success status
let success = false;
let message = '';
let articleId = null;
let articleUrl = '';

const statusCode = $('Publish to Joomla (API)').item.statusCode;

if (statusCode >= 200 && statusCode < 300) {
  success = true;
  message = 'Article successfully researched by AI agents and published to Joomla!';

  // Try to extract article ID from response
  if (joomlaResponse && joomlaResponse.data && joomlaResponse.data.id) {
    articleId = joomlaResponse.data.id;
  } else if (joomlaResponse && joomlaResponse.id) {
    articleId = joomlaResponse.id;
  }

  // Construct article URL if we have the ID
  if (articleId) {
    const baseUrl = $('Manual Trigger').item.json.joomlaUrl;
    articleUrl = `${baseUrl}/${articleData.alias || articleId}`;
  }
} else {
  message = `Failed to publish to Joomla. Status: ${statusCode}. Check your Joomla API credentials and URL.`;
}

// Calculate research quality metrics
const researchQuality = {
  sections_researched: researchData.research_sections || 0,
  trends_data_length: researchData.trends_research ? researchData.trends_research.length : 0,
  expert_analysis_length: researchData.expert_analysis ? researchData.expert_analysis.length : 0,
  best_practices_length: researchData.best_practices ? researchData.best_practices.length : 0
};

return {
  success: success,
  message: message,
  execution_summary: {
    article_title: articleData.title,
    article_alias: articleData.alias,
    article_id: articleId,
    article_url: articleUrl,
    word_count: articleData.wordCount,
    original_topic: articleData.originalTopic,
    published_at: new Date().toISOString(),
    research_method: articleData.researched_by,
    category_id: articleData.catid,
    author_id: articleData.created_by,
    seo_title: articleData.title,
    meta_description: articleData.metadesc,
    tags: articleData.metakey
  },
  research_quality: researchQuality,
  ai_agents_used: [
    'GPT-4 with Web Search (Trends Research)',
    'Claude with Web Search (Expert Analysis)', 
    'Perplexity AI (Best Practices Research)'
  ],
  joomla_response: joomlaResponse,
  status_code: statusCode,
  workflow_duration: 'Completed',
  next_steps: success ? [
    'Review published article for accuracy',
    'Share on social media platforms',
    'Monitor article performance',
    'Consider follow-up articles on related topics'
  ] : [
    'Check Joomla API credentials',
    'Verify Joomla URL and API endpoint',
    'Review article content for any formatting issues',
    'Retry publication after fixing issues'
  ]
};

