const input = $input.first();
let text = input.json.output || '';

// Extract title from the AI output
const titleMatch = text.match(/TITLE: (.+)/);
const aiTitle = titleMatch ? titleMatch[1].trim() : 'Untitled';

// Split text into chunks that fit Notion's 2000 char limit
const chunks = [];
let remainingText = text;

while (remainingText.length > 0) {
  let chunk = remainingText.substring(0, 1900);

  // If there's more text, try to break at a natural point
  if (remainingText.length > 1900) {
    const lastPeriod = chunk.lastIndexOf('. ');
    const lastNewline = chunk.lastIndexOf('\n');

    if (lastPeriod > 1200) {
      chunk = chunk.substring(0, lastPeriod + 1);
    } else if (lastNewline > 1200) {
      chunk = chunk.substring(0, lastNewline);
    }
  }

  chunks.push(chunk.trim());
  remainingText = remainingText.substring(chunk.length).trim();
}

// Return all original data plus split blocks, extracted title, and coverImageFileName
return [{
  json: {
    ...input.json,
    aiTitle: aiTitle,
    block1: chunks[0] || '',
    block2: chunks[1] || '',
    block3: chunks[2] || '',
    coverImageFileName: input.json.coverImageFileName // <-- Make sure this line is here!
  },
  binary: input.binary || {}
}];

const items = $input.all();

return items.map((item, index) => {
  console.log(`=== PROCESSING ITEM ${index + 1} ===`);
  console.log('Full item structure:', JSON.stringify(item, null, 2));

  let originalFileName = null;
  let binaryData = {};

  // Method 1: Check if binary data exists and extract filename
  if (item.binary && Object.keys(item.binary).length > 0) {
    console.log('Binary data found. Keys:', Object.keys(item.binary));

    // Usually the form field name is 'data' for file uploads
    const binaryKey = Object.keys(item.binary)[0]; // Get first binary key
    console.log('Using binary key:', binaryKey);

    if (item.binary[binaryKey]) {
      binaryData = {};
      binaryData[binaryKey] = item.binary[binaryKey];

      // Extract filename from binary data
      const binaryInfo = item.binary[binaryKey];
      console.log('Binary info:', {
        fileName: binaryInfo.fileName,
        mimeType: binaryInfo.mimeType,
        fileExtension: binaryInfo.fileExtension,
        hasData: !!binaryInfo.data
      });

      // Try multiple ways to get filename
      originalFileName = binaryInfo.fileName || 
                       binaryInfo.filename || 
                       binaryInfo.name ||
                       binaryInfo.originalname;

      console.log('Extracted filename:', originalFileName);
    }
  }

  // Method 2: Check form data structure
  if (!originalFileName && item.json) {
    console.log('Checking JSON structure for filename...');

    // Check if there's form data with filename
    if (item.json.data && Array.isArray(item.json.data)) {
      const fileField = item.json.data.find(field => 
        field.fieldName === 'data' || 
        field.fieldType === 'file' ||
        field.filename ||
        field.fileName
      );

      if (fileField) {
        originalFileName = fileField.filename || 
                         fileField.fileName || 
                         fileField.name;
        console.log('Found filename in form data:', originalFileName);
      }
    }

    // Direct filename fields
    if (!originalFileName) {
      originalFileName = item.json.filename || 
                       item.json.fileName || 
                       item.json.originalFileName ||
                       item.json.name;
      console.log('Found filename in JSON:', originalFileName);
    }
  }

  // Method 3: Generate filename if still not found - BUT KEEP IT SIMPLE
  if (!originalFileName || originalFileName === 'undefined' || originalFileName === '') {
    console.log('No filename found, using default...');
    // Use a simple default that won't cause issues
    originalFileName = 'uploaded-document.pdf';
    console.log('Using default filename:', originalFileName);
  }

  // Clean up filename (remove any problematic characters but preserve original name structure)
  // Only replace truly problematic characters, keep hyphens and underscores
  originalFileName = originalFileName.replace(/[<>:"/\\|?*]/g, '_');

  // Ensure .pdf extension
  if (!originalFileName.toLowerCase().endsWith('.pdf')) {
    const nameWithoutExt = originalFileName.split('.')[0];
    originalFileName = nameWithoutExt + '.pdf';
  }

  console.log('Final filename:', originalFileName);

  // Validation
  const hasValidFilename = originalFileName && originalFileName !== 'undefined';
  const hasValidBinary = Object.keys(binaryData).length > 0;

  console.log('Validation:');
  console.log('- Valid filename:', hasValidFilename);
  console.log('- Valid binary data:', hasValidBinary);
  console.log('- Binary data keys:', Object.keys(binaryData));

  if (hasValidBinary) {
    const firstBinaryKey = Object.keys(binaryData)[0];
    console.log('- Binary data size:', binaryData[firstBinaryKey]?.data?.length || 'unknown');
  }

  console.log('================================');

  return {
    json: {
      ...item.json,
      originalFileName: originalFileName,
      processingTimestamp: new Date().toISOString(),
      validationStatus: {
        hasFilename: hasValidFilename,
        hasBinary: hasValidBinary,
        readyForUpload: hasValidFilename && hasValidBinary
      },
      extractionMethod: 'enhanced_extraction_v3'
    },
    binary: binaryData
  };
});
